export default {
  data() {
    return {
      credentials: {},
      rules: {
        username: [v => !!v || "Usuário é obrigatório."],
        password: [
          v => !!v || "Senha é obrigatória.",
          v => (v && v.length > 5) || "A senha deve ser maior que 5 caracteres."
        ]
    }
    }
  },
  watch: {
    credentials: {
      deep: true,
      handler() {
        this.$emit('fillCredentials', this.credentials)
      }
    }
  },
  methods: {
    touch() {
      this.$v.form.$touch()
    }
  }
}
